distance_matrix = dlmread('dist_EEG.csv');N = 26;narrative = [ 0  2  4  5  7  9 11 13 16 17 18 22 25]+1;stimulus = [ 1  3  6  8 10 12 14 15 19 20 21 23 24]+1;%% cMDSclose all;[points,strainC] = cmdscale(distance_matrix,2);figure();scatter(points(narrative,1),points(narrative,2),[],'blue','filled');hold on;scatter(points(stimulus,1),points(stimulus,2),[],'red','filled');for i=1:N    text(points(i,1)+max(points,[],'all')/20,points(i,2),int2str(i-1));end%% mMDS%s=rng(2);%init = rand(N,2);%[points,stressM,disparitiesM] = mdscale(distance_matrix,2,"Criterion","metricstress","Start",init);[points,stressM,disparitiesM] = mdscale(distance_matrix,2,"Criterion","metricstress");figure();scatter(points(narrative,1),points(narrative,2),[],'blue','filled');hold on;scatter(points(stimulus,1),points(stimulus,2),[],'red','filled');for i=1:N    text(points(i,1)+max(points,[],'all')/20,points(i,2),int2str(i-1));end[dum,ord] = sortrows([disparitiesM(:) distance_matrix(:)]);computedDistancesM = pdist(points);computedDistancesM = squareform(computedDistancesM);figure()plot(distance_matrix,computedDistancesM,'bo', ...distance_matrix(ord),disparitiesM(ord),'r.-');xlabel('Dissimilarities'); ylabel('Distances');legend({'Distances' 'Disparities'},'Location','NW');corrM = corrcoef(computedDistancesM(:),disparitiesM(:));corrM = corrM(2,1);%% nMDS%s=rng(2);%init = rand(N,2);%[points,stressN,disparitiesN] = mdscale(distance_matrix,2,"Start",init);[points,stressN,disparitiesN] = mdscale(distance_matrix,2);figure();scatter(points(narrative,1),points(narrative,2),[],'blue','filled');hold on;scatter(points(stimulus,1),points(stimulus,2),[],'red','filled');for i=1:N    text(points(i,1)+max(points,[],'all')/20,points(i,2),int2str(i-1));end[dum,ord] = sortrows([disparitiesN(:) distance_matrix(:)]);computedDistancesN = pdist(points);computedDistancesN = squareform(computedDistancesN);figure()plot(distance_matrix,computedDistancesN,'bo', ...distance_matrix(ord),disparitiesN(ord),'r.-');xlabel('Dissimilarities'); ylabel('Distances/Disparities')legend({'Distances' 'Disparities'},'Location','NW');corrN = corrcoef(computedDistancesN(:),disparitiesN(:));corrN = corrN(2,1);